import React, { useState, useEffect, useRef } from 'react';
import { Link } from 'react-router-dom';
import CodeBlock from '../../Components/Common/CodeBlock';

/* =========================
   DATA DEFINITIONS
========================= */

const categories = [
  {
    id: 'getting-started',
    name: 'Getting Started',
    description: 'Learn the basics of RenderCrawl and start analyzing your JavaScript SEO.',
    articles: [
      { id: 'what-is-rendercrawl', title: 'What is RenderCrawl?' },
      { id: 'quick-start', title: 'Quick Start Guide' }
    ]
  },
  {
    id: 'features',
    name: 'Features',
    description: 'Explore the powerful rendering engine.',
    articles: [
      { id: 'javascript-rendering', title: 'Rendering Analysis' },
      { id: 'seo-metrics', title: 'SEO Metrics' }
    ]
  },
  {
    id: 'integrations',
    name: 'Integrations',
    description: 'Connect with your existing workflow.',
    articles: [
      { id: 'api-overview', title: 'API Overview' }
    ]
  }
];

const allArticles = [
  {
    id: 'what-is-rendercrawl',
    title: 'What is RenderCrawl?',
    updated: '2025-12-01',
    category: 'Getting Started',
    content: (
      <>
        <p className="mb-4">
          RenderCrawl is a specialized SEO analysis tool that focuses on JavaScript-heavy websites.
          Unlike traditional SEO tools that only analyze raw HTML, RenderCrawl uses real browser
          rendering to execute JavaScript and show you exactly what search engines see.
        </p>

        <p>
          This ensures that Single Page Applications (SPAs) built with React, Vue, or Angular are
          properly indexed and ranked.
        </p>
      </>
    )
  },
  {
    id: 'quick-start',
    title: 'Quick Start Guide',
    updated: '2025-11-28',
    category: 'Getting Started',
    content: (
      <p>
        Get started with RenderCrawl in just a few minutes. Analyze your first site and understand
        the results immediately.
      </p>
    ),
    steps: [
      { title: 'Sign Up', description: 'Create your free account.' },
      { title: 'Enter URL', description: 'Paste your website URL.' },
      { title: 'Review Results', description: 'Act on SEO insights.' }
    ]
  },
  {
    id: 'javascript-rendering',
    title: 'JavaScript Rendering Analysis',
    updated: '2025-11-15',
    category: 'Features',
    content: (
      <>
        <p className="mb-4">
          RenderCrawl uses headless Chrome to render pages exactly as users experience them.
          JavaScript frameworks such as React, Vue, and Angular are fully executed.
        </p>

        <p>
          We compare raw HTML with the rendered DOM to identify content that search engines may miss.
        </p>
      </>
    ),
    codeExample: `const ProductPage = () => {
  const [product, setProduct] = useState(null);

  useEffect(() => {
    fetchProduct().then(setProduct);
  }, []);

  return <h1>{product ? product.name : 'Loading...'}</h1>;
};`,
    tips: [
      'Use server-side rendering for critical content',
      'Ensure metadata is present in initial HTML'
    ]
  },
  {
    id: 'api-overview',
    title: 'API Overview',
    updated: '2025-10-11',
    category: 'Integrations',
    content: (
      <p>
        The RenderCrawl API allows you to integrate JavaScript SEO analysis directly into your
        workflows and CI/CD pipelines.
      </p>
    ),
    codeExample: `curl -X POST https://api.rendercrawl.com/v1/analyze \\
  -H "Authorization: Bearer YOUR_API_KEY" \\
  -d '{ "url": "https://example.com" }'`,
    tips: [
      'Never expose API keys in client-side code',
      'Use webhooks for asynchronous analysis'
    ]
  }
];

/* =========================
   ARTICLE SECTION
========================= */

const ArticleSection = ({ article }) => (
  <section id={article.id} className="scroll-mt-24 mb-12">
    <div className="bg-white rounded-3xl border border-gray-100">
      <div className="border-b border-gray-100 p-8">
        <div className="text-xs font-semibold text-blue-600 mb-2">
          {article.category}
        </div>
        <h3 className="text-3xl font-bold text-gray-900">
          {article.title}
        </h3>
        {article.updated && (
          <div className="text-sm text-gray-400 mt-1">
            Updated {article.updated}
          </div>
        )}
      </div>

      <div className="p-8">
        <div className="prose prose-lg max-w-none text-gray-700">
          {article.content}
        </div>

        {article.steps && (
          <div className="mt-8 space-y-4">
            {article.steps.map((step, i) => (
              <div key={i} className="p-4 border rounded-xl bg-gray-50">
                <h4 className="font-semibold text-gray-900">
                  {i + 1}. {step.title}
                </h4>
                <p className="text-sm text-gray-600 mt-1">
                  {step.description}
                </p>
              </div>
            ))}
          </div>
        )}

        {article.codeExample && (
          <div className="mt-8">
            <CodeBlock code={article.codeExample} filename="example.js" />
          </div>
        )}

        {article.tips && (
          <div className="mt-8 bg-blue-50 border border-blue-100 rounded-xl p-6">
            <h4 className="font-semibold text-blue-900 mb-3">
              Pro Tips
            </h4>
            <ul className="space-y-2">
              {article.tips.map((tip, i) => (
                <li key={i} className="text-sm text-blue-800">
                  â€¢ {tip}
                </li>
              ))}
            </ul>
          </div>
        )}
      </div>
    </div>
  </section>
);

/* =========================
   MAIN DOCUMENTATION PAGE
========================= */

const Documentation = () => {
  const [activeCategory, setActiveCategory] = useState('getting-started');
  const [searchQuery, setSearchQuery] = useState('');
  const [activeArticleId, setActiveArticleId] = useState(null);
  const searchRef = useRef(null);

  const activeCategoryData = categories.find(c => c.id === activeCategory);
  const q = searchQuery.trim().toLowerCase();

  const displayArticles = q
    ? allArticles.filter(a =>
        `${a.title} ${a.category}`.toLowerCase().includes(q)
      )
    : allArticles.filter(a =>
        activeCategoryData?.articles.some(ca => ca.id === a.id)
      );

  useEffect(() => {
    const observer = new IntersectionObserver(
      entries => {
        entries.forEach(e => {
          if (e.isIntersecting) setActiveArticleId(e.target.id);
        });
      },
      { rootMargin: '0px 0px -60% 0px' }
    );

    document.querySelectorAll('section[id]').forEach(el => observer.observe(el));
    return () => observer.disconnect();
  }, [displayArticles]);

  return (
    <div className="min-h-screen bg-[#F8FAFC]">
      <div className="max-w-7xl mx-auto px-6 py-16 flex gap-8">
        <aside className="w-64 hidden lg:block">
          <nav className="sticky top-24 space-y-6">
            {categories.map(cat => (
              <div key={cat.id}>
                <button
                  onClick={() => setActiveCategory(cat.id)}
                  className={`text-sm font-semibold ${
                    activeCategory === cat.id ? 'text-blue-600' : 'text-gray-500'
                  }`}
                >
                  {cat.name}
                </button>

                <ul className="mt-2 space-y-1">
                  {cat.articles.map(a => (
                    <li key={a.id}>
                      <a
                        href={`#${a.id}`}
                        onClick={e => {
                          e.preventDefault();
                          document.getElementById(a.id)?.scrollIntoView({ behavior: 'smooth' });
                        }}
                        className={`block text-sm px-3 py-2 rounded-lg ${
                          activeArticleId === a.id
                            ? 'bg-blue-50 text-blue-600'
                            : 'text-gray-600 hover:bg-gray-50'
                        }`}
                      >
                        {a.title}
                      </a>
                    </li>
                  ))}
                </ul>
              </div>
            ))}
          </nav>
        </aside>

        <main className="flex-1">
          <input
            ref={searchRef}
            value={searchQuery}
            onChange={e => setSearchQuery(e.target.value)}
            placeholder="Search documentation..."
            className="w-full mb-10 px-4 py-3 rounded-xl border focus:outline-none"
          />

          {displayArticles.map(article => (
            <ArticleSection key={article.id} article={article} />
          ))}
        </main>
      </div>
    </div>
  );
};

export default Documentation;
