import React, { useState } from 'react';

const URLInput = ({ onAnalyze, loading }) => {
  const [url, setUrl] = useState('');
  const [advancedOpen, setAdvancedOpen] = useState(false);
  const [options, setOptions] = useState({
    waitTime: 5000,
    timeout: 30000,
    fullPageScreenshot: true
  });

  const handleSubmit = (e) => {
    e.preventDefault();
    if (url && !loading && isValidURL(url)) {
      onAnalyze(url, options);
    }
  };

  const isValidURL = (string) => {
    try {
      new URL(string);
      return true;
    } catch (_) {
      return false;
    }
  };

  return (
    <div className="w-full">
      <form onSubmit={handleSubmit} className="space-y-4">
        <div className="flex flex-col sm:flex-row gap-4">
          <input
            type="text"
            value={url}
            onChange={(e) => setUrl(e.target.value)}
            placeholder="Enter website URL (e.g., https://example.com)"
            className="input-primary flex-1"
            disabled={loading}
          />
          <button 
            type="submit" 
            className="btn-primary whitespace-nowrap px-8 disabled:opacity-50 disabled:cursor-not-allowed"
            disabled={loading || !isValidURL(url)}
          >
            {loading ? (
              <div className="flex items-center space-x-2">
                <div className="w-4 h-4 border-2 border-white border-t-transparent rounded-full animate-spin"></div>
                <span>Analyzing...</span>
              </div>
            ) : (
              'Analyze SEO'
            )}
          </button>
        </div>

        {/* Advanced Options */}
        <div className="border-t border-gray-200 pt-4">
          <button
            type="button"
            className="flex items-center space-x-2 text-gray-600 hover:text-gray-900 transition-colors"
            onClick={() => setAdvancedOpen(!advancedOpen)}
          >
            <span className={`transform transition-transform ${advancedOpen ? 'rotate-180' : ''}`}>
              ▼
            </span>
            <span>Advanced Options</span>
          </button>

          {advancedOpen && (
            <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mt-4 p-4 bg-gray-50 rounded-lg">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  JS Wait Time (ms)
                </label>
                <input
                  type="number"
                  value={options.waitTime}
                  onChange={(e) => setOptions(prev => ({ 
                    ...prev, 
                    waitTime: parseInt(e.target.value) 
                  }))}
                  className="input-primary"
                  min="1000"
                  max="30000"
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Timeout (ms)
                </label>
                <input
                  type="number"
                  value={options.timeout}
                  onChange={(e) => setOptions(prev => ({ 
                    ...prev, 
                    timeout: parseInt(e.target.value) 
                  }))}
                  className="input-primary"
                  min="5000"
                  max="60000"
                />
              </div>

              <div className="flex items-center">
                <label className="flex items-center space-x-2 cursor-pointer">
                  <input
                    type="checkbox"
                    checked={options.fullPageScreenshot}
                    onChange={(e) => setOptions(prev => ({ 
                      ...prev, 
                      fullPageScreenshot: e.target.checked 
                    }))}
                    className="rounded border-gray-300 text-primary-600 focus:ring-primary-500"
                  />
                  <span className="text-sm font-medium text-gray-700">Full Page Screenshot</span>
                </label>
              </div>
            </div>
          )}
        </div>

        {!isValidURL(url) && url && (
          <div className="bg-red-50 border border-red-200 rounded-lg p-4">
            <div className="flex items-center space-x-2 text-red-700">
              <span>❌</span>
              <span>Please enter a valid URL (include http:// or https://)</span>
            </div>
          </div>
        )}
      </form>
    </div>
  );
};

export default URLInput;
