import React, { useState } from 'react';
import { useCrawler } from '../../Hooks/useCrawler';
import URLInput from '../../Components/Analyzer/URLInput/URLInput';
import CrawlProgress from '../../Components/Analyzer/CrawlProgress/CrawlProgress';


const Analyze = () => {
  const { loading, error, results, progress, analyzeURL, reset } = useCrawler();

  const handleAnalyze = async (url, options) => {
    await analyzeURL(url, options);
  };

  const handleNewAnalysis = () => {
    reset();
  };

  return (
    <div className="min-h-screen bg-gray-50 pt-16">
      <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8 py-12">
        {/* Header */}
        <div className="text-center mb-12">
          <h1 className="text-4xl font-bold text-gray-900 mb-4">
            JavaScript SEO Analyzer
          </h1>
          <p className="text-xl text-gray-600 max-w-2xl mx-auto">
            Discover how Google sees your JavaScript-heavy website. 
            Get results in <span className="font-semibold text-primary-600">30 seconds</span>.
          </p>
        </div>

        {/* Main Analyzer */}
        <div className="card p-8 mb-8">
          {!results && (
            <div className="space-y-8">
              <URLInput onAnalyze={handleAnalyze} loading={loading} />

              {loading && (
                <CrawlProgress progress={progress} currentStep={getCurrentStep(progress)} />
              )}
            </div>
          )}

          {error && (
            <div className="bg-red-50 border border-red-200 rounded-lg p-6 text-center">
              <div className="text-red-600 text-lg font-semibold mb-2">Analysis Failed</div>
              <p className="text-red-700 mb-4">{error}</p>
              <button
                onClick={handleNewAnalysis}
                className="btn-primary bg-red-500 hover:bg-red-600"
              >
                Try Again
              </button>
            </div>
          )}

          {results && (
            <div className="space-y-6">
              <div className="flex justify-between items-center">
                <h2 className="text-2xl font-bold text-gray-900">Analysis Complete</h2>
                <button
                  onClick={handleNewAnalysis}
                  className="btn-secondary"
                >
                 New Analysis
                </button>
              </div>
                       </div>
          )}
        </div>

        {/* Trust Indicators */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6 text-center">
          <div className="card p-6">
            <div className="text-2xl mb-2">ðŸ”’</div>
            <div className="font-semibold text-gray-900">Secure & Private</div>
            <div className="text-gray-600 text-sm">We never store your data</div>
          </div>
          <div className="card p-6">
            <div className="text-2xl mb-2">âš¡</div>
            <div className="font-semibold text-gray-900">Lightning Fast</div>
            <div className="text-gray-600 text-sm">30-second analysis guaranteed</div>
          </div>
          <div className="card p-6">
            <div className="text-2xl mb-2">ðŸŽ¯</div>
            <div className="font-semibold text-gray-900">Actionable Results</div>
            <div className="text-gray-600 text-sm">Clear, prioritized fixes</div>
          </div>
        </div>
      </div>
    </div>
  );
};

function getCurrentStep(progress) {
  if (progress < 30) return 'Initializing browser...';
  if (progress < 60) return 'Loading page content...';
  if (progress < 90) return 'Executing JavaScript...';
  return 'Analyzing SEO issues...';
}

export default Analyze;
