import React, { useState, useEffect, useRef } from 'react';
import { Link } from 'react-router-dom';

// Placeholder for your CodeBlock component - styled for high contrast
const CodeBlock = ({ code, filename }) => (
  <div className="my-6 rounded-lg bg-[#0d1117] overflow-hidden border border-slate-800 shadow-2xl">
    {filename && (
      <div className="px-4 py-2 bg-[#161b22] border-b border-slate-800 text-xs text-slate-400 font-mono flex justify-between">
        {filename}
        <span className="text-slate-600">javascript</span>
      </div>
    )}
    <pre className="p-4 text-sm font-mono text-blue-100 overflow-x-auto">
      <code>{code}</code>
    </pre>
  </div>
);

const categories = [
  {
    id: 'getting-started',
    name: 'Getting Started',
    articles: [
      { id: 'what-is-rendercrawl', title: 'What is RenderCrawl?', icon: 'üöÄ' },
      { id: 'quick-start', title: 'Quick Start Guide', icon: '‚ö°' },
    ]
  },
  {
    id: 'features',
    name: 'Platform Features',
    articles: [
      { id: 'javascript-rendering', title: 'Rendering Analysis', icon: 'üîç' },
      { id: 'seo-metrics', title: 'SEO Metrics', icon: 'üìä' },
    ]
  },
  {
    id: 'integrations',
    name: 'Integrations',
    articles: [
      { id: 'api-overview', title: 'API Overview', icon: 'üîå' },
    ]
  }
];

const allArticles = [
  {
    id: 'what-is-rendercrawl',
    title: 'What is RenderCrawl?',
    updated: 'Dec 1, 2025',
    category: 'Getting Started',
    content: (
      <>
        <p className="text-slate-600 leading-relaxed mb-6">
          RenderCrawl is a specialized SEO analysis engine designed for the modern web. Traditional crawlers often fail to execute JavaScript, missing content within <strong>Single Page Applications (SPAs)</strong>. RenderCrawl solves this by using headless browser technology to see exactly what a human sees.
        </p>

      </>
    )
  },
  {
    id: 'javascript-rendering',
    title: 'JavaScript Rendering Analysis',
    updated: 'Nov 15, 2025',
    category: 'Features',
    codeExample: `// Example: RenderCrawl detects dynamic content
const ProductPage = () => {
  const [data, setData] = useState(null);
  useEffect(() => { fetchAPI().then(setData); }, []);

  return <div>{data ? data.name : 'Loading...'}</div>;
};`,
    content: (
      <>
        <p className="text-slate-600 leading-relaxed mb-6">
          We utilize a "Render & Compare" methodology. We fetch the raw HTML source and then perform a full render of the DOM after JavaScript execution.
        </p>

        <p className="text-slate-600 leading-relaxed mt-6">
          This allows us to flag "Content Gaps"‚Äîsections of your site that exist for users but are invisible to search engine bots that lack JS execution capabilities.
        </p>
      </>
    )
  },
  {
    id: 'api-overview',
    title: 'API Overview',
    updated: 'Oct 11, 2025',
    category: 'Integrations',
    codeExample: `curl -X POST https://api.rendercrawl.com/v1/analyze \\
  -H "Authorization: Bearer YOUR_API_KEY" \\
  -d '{ "url": "https://example.com" }'`,
    content: (
      <>
        <p className="text-slate-600 leading-relaxed mb-6">
          The RenderCrawl API follows RESTful principles and returns structured JSON containing both SEO metadata and the fully rendered HTML string.
        </p>

      </>
    )
  }
];

const Documentation = () => {
  const [activeArticleId, setActiveArticleId] = useState('what-is-rendercrawl');
  const [searchQuery, setSearchQuery] = useState('');
  const searchRef = useRef(null);

  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) setActiveArticleId(entry.target.id);
        });
      },
      { rootMargin: '-20% 0% -70% 0%' }
    );
    document.querySelectorAll('section[id]').forEach((s) => observer.observe(s));
    return () => observer.disconnect();
  }, []);

  const filteredArticles = allArticles.filter(a => 
    a.title.toLowerCase().includes(searchQuery.toLowerCase())
  );

  return (
    <div className="min-h-screen bg-white text-slate-900 selection:bg-blue-100">
      {/* --- Top Navigation --- */}
      <header className="sticky top-0 z-50 w-full border-b border-slate-200 bg-white/80 backdrop-blur-md">
        <div className="mx-auto flex h-16 max-w-[1440px] items-center justify-between px-6">
          <div className="flex items-center gap-8">
            <Link to="/" className="text-xl font-bold tracking-tight text-slate-900">
              Render<span className="text-blue-600">Crawl</span> <span className="text-slate-400 font-normal ml-1">Docs</span>
            </Link>
            <div className="hidden md:flex relative group">
              <input 
                ref={searchRef}
                type="text"
                placeholder="Search documentation..."
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                className="w-64 lg:w-80 rounded-full border border-slate-200 bg-slate-50 px-4 py-1.5 text-sm transition-all focus:border-blue-500 focus:bg-white focus:ring-2 focus:ring-blue-100"
              />
              <kbd className="absolute right-3 top-1.5 rounded border border-slate-200 bg-white px-1.5 py-0.5 text-[10px] font-medium text-slate-400">‚åòK</kbd>
            </div>
          </div>
          <div className="flex items-center gap-6 text-sm font-medium text-slate-600">
            <a href="#" className="hover:text-blue-600">v1.2.0</a>
            <a href="#" className="hover:text-blue-600">Support</a>
            <button className="rounded-lg bg-slate-900 px-4 py-2 text-white hover:bg-slate-800 transition-all">Dashboard</button>
          </div>
        </div>
      </header>

      <div className="mx-auto max-w-[1440px] px-6">
        <div className="flex flex-col lg:flex-row gap-12">

          {/* --- Left Sidebar (Nav) --- */}
          <aside className="hidden lg:block w-64 flex-shrink-0 pt-12 sticky top-16 h-[calc(100vh-64px)] overflow-y-auto pb-10 custom-scrollbar">
            <nav className="space-y-8">
              {categories.map((cat) => (
                <div key={cat.id}>
                  <h4 className="text-xs font-bold uppercase tracking-widest text-slate-400 mb-4">{cat.name}</h4>
                  <ul className="space-y-2 border-l border-slate-100">
                    {cat.articles.map((art) => (
                      <li key={art.id}>
                        <a 
                          href={`#${art.id}`}
                          onClick={(e) => {
                            e.preventDefault();
                            document.getElementById(art.id).scrollIntoView({ behavior: 'smooth' });
                          }}
                          className={`group -ml-px flex items-center border-l-2 pl-4 py-1 text-sm transition-all ${
                            activeArticleId === art.id 
                              ? 'border-blue-600 text-blue-600 font-semibold' 
                              : 'border-transparent text-slate-500 hover:border-slate-300 hover:text-slate-900'
                          }`}
                        >
                          <span className="mr-2 opacity-70 group-hover:opacity-100">{art.icon}</span>
                          {art.title}
                        </a>
                      </li>
                    ))}
                  </ul>
                </div>
              ))}
            </nav>
          </aside>

          {/* --- Main Content --- */}
          <main className="flex-1 py-12 min-w-0 max-w-3xl">
            {filteredArticles.length > 0 ? (
              filteredArticles.map((article) => (
                <section key={article.id} id={article.id} className="mb-24 scroll-mt-28">
                  <div className="flex items-center gap-2 text-xs font-bold text-blue-600 uppercase tracking-widest mb-3">
                    <span className="bg-blue-50 px-2 py-0.5 rounded">{article.category}</span>
                    <span className="text-slate-300">‚Ä¢</span>
                    <span className="text-slate-400 font-medium lowercase italic">Updated {article.updated}</span>
                  </div>
                  <h2 className="text-4xl font-extrabold tracking-tight text-slate-900 mb-6">{article.title}</h2>

                  <div className="prose prose-slate prose-blue max-w-none">
                    {article.content}
                  </div>

                  {article.codeExample && (
                    <div className="mt-8">
                      <h4 className="text-sm font-bold text-slate-900 mb-3 flex items-center gap-2">
                        <svg className="w-4 h-4 text-slate-400" fill="none" stroke="currentColor" viewBox="0 0 24 24"><path d="M10 20l4-16m4 4l4 4-4 4M6 16l-4-4 4-4" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/></svg>
                        Implementation
                      </h4>
                      <CodeBlock code={article.codeExample} />
                    </div>
                  )}

                  <hr className="mt-16 border-slate-100" />
                </section>
              ))
            ) : (
              <div className="text-center py-20">
                <h3 className="text-lg font-semibold">No results for "{searchQuery}"</h3>
                <button onClick={() => setSearchQuery('')} className="text-blue-600 underline mt-2">Clear search</button>
              </div>
            )}
          </main>

          {/* --- Right Sidebar (TOC) --- */}
          <aside className="hidden xl:block w-64 flex-shrink-0 pt-12 sticky top-16 h-[calc(100vh-64px)]">
            <h5 className="text-[11px] font-bold uppercase tracking-[0.2em] text-slate-400 mb-5">On this page</h5>
            <ul className="space-y-4 text-[13px] font-medium text-slate-500">
              {filteredArticles.map(art => (
                <li key={art.id}>
                  <a 
                    href={`#${art.id}`}
                    className={`block transition-colors hover:text-slate-900 ${activeArticleId === art.id ? 'text-blue-600' : ''}`}
                  >
                    {art.title}
                  </a>
                </li>
              ))}
            </ul>
            <div className="mt-12 p-5 bg-slate-50 rounded-2xl border border-slate-100">
              <h6 className="text-sm font-bold text-slate-900 mb-2">Need a custom plan?</h6>
              <p className="text-xs text-slate-500 leading-relaxed mb-4">Enterprise-grade JS rendering for high-volume sites.</p>
              <button className="text-xs font-bold text-blue-600 flex items-center gap-1 hover:gap-2 transition-all">
                Talk to Sales <span>‚Üí</span>
              </button>
            </div>
          </aside>

        </div>
      </div>
    </div>
  );
};

export default Documentation;
