import React, { useState, useEffect, useRef } from 'react';
import { Link } from 'react-router-dom';

// --- Data Definitions ---

const categories = [
  {
    id: 'getting-started',
    name: 'Getting Started',
    description: 'Learn the basics of RenderCrawl and start analyzing your JavaScript SEO.',
    articles: [
      { id: 'what-is-rendercrawl', title: 'What is RenderCrawl?', icon: 'üöÄ' },
      { id: 'quick-start', title: 'Quick Start Guide', icon: '‚ö°' },
    ]
  },
  {
    id: 'features',
    name: 'Features',
    description: 'Explore the powerful rendering engine and SEO metrics.',
    articles: [
      { id: 'javascript-rendering', title: 'Rendering Analysis', icon: 'üîç' },
      { id: 'seo-metrics', title: 'SEO Metrics', icon: 'üìä' },
    ]
  },
  {
    id: 'integrations',
    name: 'Integrations',
    description: 'Connect with your existing workflow via our API.',
    articles: [
      { id: 'api-overview', title: 'API Overview', icon: 'üîå' },
    ]
  }
];

const allArticles = [
  {
    id: 'what-is-rendercrawl',
    title: 'What is RenderCrawl?',
    updated: 'Dec 01, 2025',
    category: 'Getting Started',
    content: (
      <>
        <p>RenderCrawl is a specialized SEO analysis tool that focuses on JavaScript-heavy websites. Unlike traditional SEO tools that only analyze raw HTML, RenderCrawl uses real browser rendering to execute JavaScript and show you exactly what Google sees.</p>
        <div className="my-10 p-8 bg-slate-50 rounded-2xl border border-slate-100 text-center">
          <div className="text-slate-400 italic text-sm mb-2">[Diagram: Raw HTML vs. Rendered DOM Comparison]</div>
          <p className="text-xs text-slate-500">Visualizing how SPAs appear empty to basic crawlers but full to RenderCrawl.</p>
        </div>
      </>
    ),
  },
  {
    id: 'quick-start',
    title: 'Quick Start Guide',
    updated: 'Nov 28, 2025',
    category: 'Getting Started',
    content: <p>Get started with RenderCrawl in just 5 minutes. Learn how to analyze your first website and interpret the results to improve your JavaScript SEO.</p>,
    steps: [
      { title: 'Sign Up', description: 'Create your free account on the dashboard.' },
      { title: 'Enter URL', description: 'Paste your website URL into the analyzer.' },
      { title: 'Review Results', description: 'Get actionable insights into your JS execution.' }
    ]
  },
  {
    id: 'javascript-rendering',
    title: 'JavaScript Rendering Analysis',
    updated: 'Nov 15, 2025',
    category: 'Features',
    content: (
      <>
        <p>RenderCrawl uses headless browsers to render your web pages. We wait for all JavaScript to execute, including React, Vue, Angular, and other frameworks.</p>
        <div className="my-10 p-8 bg-slate-900 rounded-2xl text-center text-white">
          <div className="text-blue-400 font-mono text-sm mb-2">Rendering Pipeline</div>
          <p className="text-xs text-slate-400">Request ‚Üí JS Execution ‚Üí DOM Snapshot ‚Üí SEO Audit</p>
        </div>
      </>
    ),
    codeExample: `// Example: How we handle async data
const Product = () => {
  const [data, setData] = useState(null);
  useEffect(() => { fetch().then(setData) }, []);
  return <div>{data ? data.name : 'Loading...'}</div>;
};`,
    tips: ['Use SSR for critical metadata', 'Minimize main-thread blocking JS']
  },
  {
    id: 'api-overview',
    title: 'API Overview',
    updated: 'Oct 11, 2025',
    category: 'Integrations',
    content: <p>The RenderCrawl API allows you to programmatically analyze websites and integrate SEO testing into your CI/CD pipeline.</p>,
    codeExample: `curl -X POST https://api.rendercrawl.com/v1/analyze \\
-H "Authorization: Bearer YOUR_API_KEY" \\
-d '{ "url": "https://example.com" }'`,
  }
];

// --- Sub-Components ---

const Icon = ({ name }) => {
  const icons = {
    menu: <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 6h16M4 12h16m-7 6h7" /></svg>,
    close: <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" /></svg>,
    search: <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z" /></svg>,
    chevronRight: <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2.5} d="M9 5l7 7-7 7" /></svg>,
    chevronLeft: <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2.5} d="M15 19l-7-7 7-7" /></svg>,
  };
  return icons[name] || null;
};

// --- Main Page Component ---

const Documentation = () => {
  const [activeCategory, setActiveCategory] = useState('getting-started');
  const [activeArticleId, setActiveArticleId] = useState(allArticles[0].id);
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const [searchQuery, setSearchQuery] = useState('');
  const searchRef = useRef(null);

  // Search logic
  const q = searchQuery.trim().toLowerCase();
  const displayArticles = q 
    ? allArticles.filter(a => a.title.toLowerCase().includes(q) || a.category.toLowerCase().includes(q))
    : allArticles.filter(a => categories.find(c => c.id === activeCategory)?.articles.some(ca => ca.id === a.id));

  // Pagination logic
  const currentIndex = allArticles.findIndex(a => a.id === activeArticleId);
  const prevArticle = currentIndex > 0 ? allArticles[currentIndex - 1] : null;
  const nextArticle = currentIndex < allArticles.length - 1 ? allArticles[currentIndex + 1] : null;

  // Interaction: Scroll Spy
  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) setActiveArticleId(entry.target.id);
        });
      },
      { rootMargin: '0px 0px -70% 0px', threshold: 0.1 }
    );
    document.querySelectorAll('article[id]').forEach((el) => observer.observe(el));
    return () => observer.disconnect();
  }, [displayArticles]);

  const scrollTo = (id) => {
    const el = document.getElementById(id);
    if (el) {
      const offset = 100;
      const bodyRect = document.body.getBoundingClientRect().top;
      const elementRect = el.getBoundingClientRect().top;
      const elementPosition = elementRect - bodyRect;
      const offsetPosition = elementPosition - offset;

      window.scrollTo({ top: offsetPosition, behavior: 'smooth' });
    }
    setIsMobileMenuOpen(false);
  };

  return (
    <div className="flex flex-col min-h-screen bg-white font-sans text-slate-900 selection:bg-blue-100 selection:text-blue-900">

      {/* 1. Header & Mobile Nav Bar */}
      <header className="sticky top-0 z-50 w-full border-b border-slate-100 bg-white/80 backdrop-blur-lg">
        <div className="max-w-[1440px] mx-auto px-4 lg:px-8 h-16 flex items-center justify-between">
          <div className="flex items-center gap-4">
            <button 
              onClick={() => setIsMobileMenuOpen(!isMobileMenuOpen)}
              className="lg:hidden p-2 -ml-2 text-slate-600 hover:bg-slate-50 rounded-xl transition-colors"
            >
              <Icon name={isMobileMenuOpen ? "close" : "menu"} />
            </button>
            <Link to="/" className="text-xl font-black tracking-tighter text-blue-600">RENDERCRAWL</Link>
          </div>

          <div className="relative hidden md:block w-72">
            <span className="absolute inset-y-0 left-3 flex items-center text-slate-400">
              <Icon name="search" />
            </span>
            <input 
              ref={searchRef}
              type="text"
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              placeholder="Search guides..."
              className="w-full pl-10 pr-4 py-2 text-sm bg-slate-100 border-none rounded-full focus:bg-white focus:ring-2 focus:ring-blue-500 transition-all outline-none"
            />
          </div>
        </div>
      </header>

      <div className="flex flex-1 max-w-[1440px] mx-auto w-full">

        {/* 2. Sidebar (Desktop & Mobile Drawer) */}
        <aside className={`
          fixed inset-0 top-16 z-40 bg-white lg:static lg:block lg:w-72 lg:border-r lg:border-slate-50 p-6 lg:p-8 overflow-y-auto transition-transform duration-300
          ${isMobileMenuOpen ? 'translate-x-0' : '-translate-x-full lg:translate-x-0'}
        `}>
          <div className="md:hidden mb-6">
            <input 
              type="text"
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              placeholder="Search documentation..."
              className="w-full p-3 bg-slate-100 rounded-xl text-sm outline-none"
            />
          </div>

          {categories.map((cat) => (
            <div key={cat.id} className="mb-10">
              <h4 className="text-[11px] font-bold text-slate-400 uppercase tracking-[0.2em] mb-4 ml-3">
                {cat.name}
              </h4>
              <ul className="space-y-1">
                {cat.articles.map((art) => (
                  <li key={art.id}>
                    <button
                      onClick={() => {
                        setActiveCategory(cat.id);
                        scrollTo(art.id);
                      }}
                      className={`w-full text-left px-4 py-2.5 rounded-xl text-sm transition-all ${
                        activeArticleId === art.id 
                          ? 'bg-blue-50 text-blue-700 font-bold' 
                          : 'text-slate-500 hover:text-slate-900 hover:bg-slate-50'
                      }`}
                    >
                      <span className="mr-3">{art.icon}</span>
                      {art.title}
                    </button>
                  </li>
                ))}
              </ul>
            </div>
          ))}
        </aside>

        {/* 3. Main Content */}
        <main className="flex-1 px-4 md:px-12 lg:px-20 py-12 min-w-0">
          <div className="max-w-3xl">

            {/* Articles List */}
            <div className="space-y-32">
              {displayArticles.map((article) => (
                <article key={article.id} id={article.id} className="scroll-mt-32">
                  <div className="flex items-center gap-3 mb-6">
                    <span className="px-2 py-0.5 bg-blue-50 text-blue-600 text-[10px] font-bold uppercase rounded leading-4">Guide</span>
                    <span className="text-slate-200">|</span>
                    <span className="text-xs text-slate-400">Last updated {article.updated}</span>
                  </div>

                  <h2 className="text-4xl font-extrabold text-slate-900 tracking-tight mb-8">
                    {article.title}
                  </h2>

                  <div className="prose prose-slate max-w-none text-slate-600 leading-relaxed text-lg prose-p:mb-6">
                    {article.content}
                  </div>

                  {article.steps && (
                    <div className="mt-12 space-y-4">
                      {article.steps.map((step, i) => (
                        <div key={i} className="flex gap-5 p-6 bg-slate-50 rounded-2xl border border-slate-100">
                          <div className="flex-shrink-0 w-8 h-8 bg-white border border-slate-200 rounded-lg flex items-center justify-center font-bold text-blue-600 shadow-sm">
                            {i + 1}
                          </div>
                          <div>
                            <h4 className="font-bold text-slate-900 mb-1">{step.title}</h4>
                            <p className="text-sm text-slate-500 leading-relaxed">{step.description}</p>
                          </div>
                        </div>
                      ))}
                    </div>
                  )}

                  {article.codeExample && (
                    <div className="mt-10 rounded-2xl overflow-hidden border border-slate-200 shadow-sm">
                      <div className="bg-slate-50 px-4 py-2 border-b border-slate-200 text-[11px] font-mono text-slate-400 uppercase tracking-widest">
                        Example Code
                      </div>
                      <pre className="p-6 bg-[#0B1120] text-blue-100 text-sm overflow-x-auto font-mono leading-relaxed">
                        <code>{article.codeExample}</code>
                      </pre>
                    </div>
                  )}

                  {article.tips && (
                    <div className="mt-10 grid grid-cols-1 md:grid-cols-2 gap-4">
                      {article.tips.map((tip, i) => (
                        <div key={i} className="flex gap-3 p-5 bg-emerald-50/50 rounded-2xl border border-emerald-100">
                          <div className="text-emerald-500 mt-0.5">
                            <svg className="w-5 h-5" fill="currentColor" viewBox="0 0 20 20"><path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zm3.707-9.293a1 1 0 00-1.414-1.414L9 10.586 7.707 9.293a1 1 0 00-1.414 1.414l2 2a1 1 0 001.414 0l4-4z" clipRule="evenodd" /></svg>
                          </div>
                          <p className="text-sm text-emerald-800 font-medium">{tip}</p>
                        </div>
                      ))}
                    </div>
                  )}
                </article>
              ))}
            </div>

            {/* 4. Refined Navigation Footer */}
            {!q && (
              <>
                <div className="mt-32 pt-12 border-t border-slate-100 grid grid-cols-1 sm:grid-cols-2 gap-6 pb-24">
                  {prevArticle ? (
                    <button 
                      onClick={() => scrollTo(prevArticle.id)}
                      className="group p-6 rounded-2xl border border-slate-100 hover:border-blue-500 hover:bg-blue-50/30 transition-all text-left"
                    >
                      <span className="flex items-center gap-2 text-xs font-bold text-slate-400 uppercase tracking-widest mb-3">
                        <Icon name="chevronLeft" /> Previous
                      </span>
                      <span className="text-lg font-bold text-slate-900 group-hover:text-blue-600 transition-colors">
                        {prevArticle.title}
                      </span>
                    </button>
                  ) : <div />}

                  {nextArticle ? (
                    <button 
                      onClick={() => scrollTo(nextArticle.id)}
                      className="group p-6 rounded-2xl border border-slate-100 hover:border-blue-500 hover:bg-blue-50/30 transition-all text-right"
                    >
                      <span className="flex items-center justify-end gap-2 text-xs font-bold text-slate-400 uppercase tracking-widest mb-3">
                        Next Up <Icon name="chevronRight" />
                      </span>
                      <span className="text-lg font-bold text-slate-900 group-hover:text-blue-600 transition-colors">
                        {nextArticle.title}
                      </span>
                    </button>
                  ) : (
                    <Link to="/contact" className="group p-6 rounded-2xl bg-slate-900 text-white hover:bg-slate-800 transition-all shadow-xl shadow-slate-200">
                      <span className="text-xs font-bold opacity-50 uppercase tracking-widest mb-3 block">End of Documentation</span>
                      <span className="text-lg font-bold flex items-center justify-between">
                        Need more help? <Icon name="chevronRight" />
                      </span>
                    </Link>
                  )}
                </div>
              </>
            )}
          </div>
        </main>

        {/* 5. Table of Contents (Desktop Only) */}
        <aside className="hidden xl:block w-64 p-8 sticky top-16 h-[calc(100vh-64px)] overflow-y-auto">
          <h5 className="text-[11px] font-bold text-slate-400 uppercase tracking-widest mb-6 ml-1">
            On this page
          </h5>
          <nav className="border-l border-slate-100">
            <ul className="space-y-4">
              {displayArticles.map((art) => (
                <li key={art.id} className="relative">
                  {activeArticleId === art.id && (
                    <div className="absolute left-[-1.5px] top-0 bottom-0 w-[2px] bg-blue-600 rounded-full" />
                  )}
                  <button
                    onClick={() => scrollTo(art.id)}
                    className={`block pl-5 text-xs text-left transition-all ${
                      activeArticleId === art.id 
                        ? 'text-blue-600 font-bold' 
                        : 'text-slate-500 hover:text-slate-900'
                    }`}
                  >
                    {art.title}
                  </button>
                </li>
              ))}
            </ul>
          </nav>
        </aside>

      </div>
    </div>
  );
};

export default Documentation;
